def fun_action(num):
	
	num = num.strip()
	
	if num.isnumeric() and "+" not in num:
		num = "+%s" % num
	elif "@" in num:
		pass
	else:
		pass
			
	while True:
		try:
			A , Error = Amazon(num).check()
			
			if A:
				with open("Valid.txt","a") as ff:
					ff.write("%s\n" % num)
				print("[+] Yes ==> %s"  % num) 
				break
				
			else:
				
				print("[-] No ==> %s"  % num) 
				break
		except:pass
		
def main():
	
	# ~ INDEx()
	
	email = open( input("[-] List Name : ")  ,"r",encoding="Latin-1").read().splitlines()
	ThreadPool = Pool(2)
	ThreadPool.map(fun_action, email)


